package com.example.football

import android.annotation.SuppressLint
import android.support.v7.app.AppCompatActivity
import android.os.Bundle
import android.support.constraint.ConstraintLayout
import android.support.design.widget.NavigationView
import android.support.v4.content.ContextCompat
import android.support.v4.widget.DrawerLayout
import android.support.v7.widget.LinearLayoutManager
import android.support.v7.widget.RecyclerView
import android.util.Log
import android.view.LayoutInflater
import android.view.MenuItem
import android.view.View
import android.view.ViewManager
import android.widget.LinearLayout
import android.widget.RelativeLayout
import android.widget.TextView
import android.widget.Toast
import com.bumptech.glide.Glide
import com.loopj.android.http.AsyncHttpClient
import com.loopj.android.http.JsonHttpResponseHandler
import cz.msebera.android.httpclient.Header
import de.hdodenhof.circleimageview.CircleImageView
import org.json.JSONObject

class MainActivity : AppCompatActivity() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var viewAdapter: RecyclerView.Adapter<*>
    private lateinit var viewManager: RecyclerView.LayoutManager
    private lateinit var navigationView: NavigationView
    private lateinit var drawerLayout: DrawerLayout
    private lateinit var date: String
    private lateinit var url: String
    private var clubNameDatabase: ArrayList<String> = ArrayList()
    private var iconDatabase: ArrayList<String> = ArrayList()
    private val TAG: String = "MainActivity"


    @SuppressLint("ResourceType")
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        Log.d(TAG,"OnCreate: started.")
        navigationView= findViewById(R.id.nav_view)
        navigationView.itemIconTintList = null
        drawerLayout = findViewById(R.id.drawer_layout)

        navigationView.setNavigationItemSelectedListener { menuItem ->

            menuItem.isChecked

            drawerLayout.closeDrawers()

            true
        }

        date = "2018-03-13"
        var networking = Networking()
        networking.pickDateUrl(date)
        url=networking.getUrl()

        //initImageBitmaps()
        letsDoSomeNetworing(url)


        }

    private fun letsDoSomeNetworing(myurl: String){

        var client = AsyncHttpClient()

        var netobj=Networking()
        client.get(myurl, object : JsonHttpResponseHandler() {

            override fun onSuccess(statusCode: Int, headers: Array<Header>?, response: JSONObject?) {
                // called when response HTTP status is "200 OK"
                Log.d(TAG, "JSON: " + response!!.toString())
                netobj = Networking.networkingWithJson(response)
                initArrays(netobj)
            }

            override fun onFailure(statusCode: Int, headers: Array<Header>?, e: Throwable, response: JSONObject?) {
                // called when response HTTP status is "4XX" (eg. 401, 403, 404)
                Log.d(TAG, "Request fail! Status code: $statusCode")
                Log.d(TAG, "Fail response: " + response!!)
                Log.e("ERROR", e.toString())
                Toast.makeText(this@MainActivity, "Request Failed", Toast.LENGTH_SHORT).show()
            }
        })


    }

    private fun initArrays(netobj: Networking){
        Log.d(TAG,"initImageBitmaps: preparing bitmaps")
        iconDatabase.add("data:image/jpeg;base64,")
        clubNameDatabase.add(netobj.clubNameDatabase[0])
        Log.d(TAG,""+iconDatabase[0])
        Log.d(TAG,""+clubNameDatabase[0])
        initRecyclerView()
    }

    @SuppressLint("ResourceType")
    private fun initRecyclerView(){
        viewManager = LinearLayoutManager(this)
        viewAdapter = MyAdapter(clubNameDatabase,iconDatabase)

        recyclerView = findViewById<RecyclerView>(R.id.my_recycler_view).apply {

            setHasFixedSize(true)
            layoutManager=viewManager
            adapter=viewAdapter
        }
    }


}

